import React, { useRef, useEffect } from 'react';
import './Sidebar.css';

const Sidebar = ({ isSidebarOpen, onClose }) => {
  const sidebarRef = useRef();

  const handleOutsideClick = (e) => {
    const button = document.querySelector('.sidebar-toggle-button'); // Add a class to your button

    if (
      sidebarRef.current &&
      !sidebarRef.current.contains(e.target) &&
      e.target !== button
    ) {
      onClose();
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleOutsideClick);

    return () => {
      document.removeEventListener('mousedown', handleOutsideClick);
    };
  }, [onClose]);

  return (
    <div ref={sidebarRef} className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
      <div style={{ width: isSidebarOpen ? '100%' : '0', margin: 0 }}>
      
        <a className='button' href="https://www.w3schools.com"><img src="data:image/png;base64," alt="" />W3Schools</a>
     
          <a className='button' href="https://react.dev/blog/2023/03/16/introducing-react-dev">
            <img src="data:image/png;base64," alt="" />
            React Dev</a>
    
        <hr />
   
          <a className='button' href="https://www.javatpoint.com/reactjs-tutorial">
            <img src="data:image/png;base64," alt="" /> Javatpoint
          </a>
      
      
          <a className='button'  href="https://www.tutorialspoint.com/reactjs/index.htm">
            <img src="data:image/png;base64," alt="" />
            Tutorial point</a>
     


      </div>
    </div>
  );
};

export default Sidebar;
